import React, { Component } from 'react'
import { Slide } from 'react-slideshow-image'

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
  pauseOnHover: true,
  onChange: (oldIndex, newIndex) => {
    console.log(`slide transition from ${oldIndex} to ${newIndex}`)
  }
}

class Artwork extends Component {
  render () {
    if (this.props.data) {
      var artworks = this.props.data.artworks.map(function (artworks) {
        var artwork = 'images/artwork/' + artworks.image
        return <div style={{ backgroundImage: `url(${artwork})` }}>
          <span>Slide</span>
        </div>
      })

      return (
        <section id='artwork'>
          <div className='slide-container'>
            <Slide {...properties}>
              <div className='each-slide'>
                {artworks}
              </div>
            </Slide>
          </div>
        </section>
      )
    }
  }
}

export default Artwork
