import React, { Component } from "react";
import { Slide } from "react-slideshow-image";

const artworks = ["images//artwork/1.jpg", "images//artwork/2.jpg"];

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
  pauseOnHover: true,
  onChange: (oldIndex, newIndex) => {
    console.log(`slide transition from ${oldIndex} to ${newIndex}`);
  },
};

class Artwork extends Component {
  render() {
    // if (this.props.data) {
    //   var artworks = this.props.data.artwork.map(function (piece) {
    //     var artwork = 'images/artwork/' + piece.image
    //     return [artwork]
    //   })
    // }

    return (
      <section id="artwork">
        <div className="slide-container">
          <Slide {...properties}>
            <div className="each-slide">
              <div style={{ backgroundImage: `url(${artworks[0]})` }}>
                <span>Slide 1</span>
              </div>
            </div>
            <div className="each-slide">
              <div style={{ backgroundImage: `url(${artworks[1]})` }}></div>
            </div>
          </Slide>
        </div>
      </section>
    );
  }
}

export default Artwork;
